import React from 'react';
import cx from 'clsx';
import { BookmarkIcon, HeartIcon, ShareIcon } from '@primer/octicons-react';
import {
  Card,
  Image,
  Text,
  ActionIcon,
  Badge,
  Group,
  Center,
  Avatar,
  useMantineTheme,
  createStyles,
} from '@mantine/core';

const useStyles = createStyles((theme) => ({
  card: {
    position: 'relative',
  },

  rating: {
    position: 'absolute',
    top: theme.spacing.xs,
    right: theme.spacing.xs + 2,
    pointerEvents: 'none',
  },

  title: {
    display: 'block',
    marginTop: theme.spacing.xs,
    marginBottom: theme.spacing.xs / 2,
  },

  action: {
    backgroundColor: theme.colorScheme === 'dark' ? theme.colors.dark[7] : theme.colors.gray[0],
  },

  footer: {
    marginTop: theme.spacing.xs,
  },
}));

interface ArticleCardProps {
  image: string;
  link: string;
  title: string;
  description: string;
  rating: string;
  author: {
    name: string;
    image: string;
  };
}

export function ArticleCard({
  className,
  image,
  link,
  title,
  description,
  author,
  rating,
  ...others
}: ArticleCardProps & Omit<React.ComponentPropsWithoutRef<'div'>, keyof ArticleCardProps>) {
  const classes = useStyles();
  const theme = useMantineTheme();

  return (
    <Card shadow="sm" className={cx(classes.card, className)} {...others}>
      <Card.Section>
        <a href={link} target="_blank" rel="noreferrer">
          <Image src={image} height={180} />
        </a>
      </Card.Section>

      <Badge
        className={classes.rating}
        variant="gradient"
        gradient={{ from: 'indigo', to: 'cyan' }}
      >
        {rating}
      </Badge>

      <Text className={classes.title} weight={500} component="a" href={link} target="_blank">
        {title}
      </Text>

      <Text size="sm" color="dimmed" lineClamp={4}>
        {description}
      </Text>

      <Group position="apart" className={classes.footer}>
        <Center>
          <Avatar src={author.image} size={24} radius="xl" style={{ marginRight: 8 }} />
          <Text size="sm" inline>
            {author.name}
          </Text>
        </Center>

        <Group spacing={8} style={{ marginRight: 0 }}>
          <ActionIcon className={classes.action} style={{ color: theme.colors.red[6] }}>
            <HeartIcon />
          </ActionIcon>
          <ActionIcon className={classes.action} style={{ color: theme.colors.yellow[7] }}>
            <BookmarkIcon />
          </ActionIcon>
          <ActionIcon className={classes.action}>
            <ShareIcon />
          </ActionIcon>
        </Group>
      </Group>
    </Card>
  );
}

import React from 'react';
import cx from 'clsx';
import {
  createStyles,
  Card,
  Overlay,
  CardProps,
  Button,
  Text,
  useMantineTheme,
} from '@mantine/core';

const useStyles = createStyles((theme) => ({
  card: {
    height: 240,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
  },

  content: {
    position: 'absolute',
    padding: theme.spacing.xl,
    zIndex: 1,
    top: 0,
    bottom: 0,
    right: 0,
    left: 0,
  },

  action: {
    position: 'absolute',
    bottom: theme.spacing.xl,
    right: theme.spacing.xl,
  },

  title: {
    color: theme.white,
    marginBottom: theme.spacing.xs / 2,
  },

  description: {
    color: theme.white,
    maxWidth: 220,
  },
}));

interface ImageActionCardProps {
  title: React.ReactNode;
  description: React.ReactNode;
  image: string;
  action: {
    label: string;
    link: string;
  };
}

export function ImageActionCard({
  title,
  description,
  image,
  action,
  style,
  className,
  ...others
}: ImageActionCardProps &
  Omit<CardProps<'div', HTMLDivElement>, keyof ImageActionCardProps | 'children'>) {
  const classes = useStyles();
  const theme = useMantineTheme();

  return (
    <Card
      radius="md"
      {...others}
      style={{ backgroundImage: `url(${image})`, ...style }}
      className={cx(classes.card, className)}
    >
      <Overlay
        gradient={`linear-gradient(105deg, ${theme.black} 20%, #312f2f 50%, ${theme.colors.gray[4]} 100%)`}
        opacity={0.55}
        zIndex={0}
      />

      <div className={classes.content}>
        <Text size="lg" weight={700} className={classes.title}>
          {title}
        </Text>

        <Text size="sm" className={classes.description}>
          {description}
        </Text>

        <Button
          className={classes.action}
          variant="white"
          color="dark"
          component="a"
          href={action.link}
          size="xs"
        >
          {action.label}
        </Button>
      </div>
    </Card>
  );
}

import React from 'react';
import { EyeOpenIcon, ChatBubbleIcon } from '@modulz/radix-icons';
import { Card, Text, Group, Center, createStyles } from '@mantine/core';

const useStyles = createStyles((theme) => ({
  card: {
    position: 'relative',
    height: 280,
    backgroundColor: theme.colorScheme === 'dark' ? theme.colors.dark[6] : theme.colors.gray[0],

    '&:hover $image': {
      transform: 'scale(1.03)',
    },
  },

  image: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundSize: 'cover',
    transition: 'transform 500ms ease',
  },

  overlay: {
    position: 'absolute',
    top: '20%',
    left: 0,
    right: 0,
    bottom: 0,
    backgroundImage: 'linear-gradient(180deg, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, .85) 90%)',
  },

  content: {
    height: '100%',
    position: 'relative',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'flex-end',
    zIndex: 1,
  },

  title: {
    color: theme.white,
  },

  body: {
    color: theme.colors.gray[3],
    marginTop: 0,
  },

  bodyText: {
    color: theme.colors.dark[2],
  },
}));

interface ImageCardProps {
  link: string;
  image: string;
  title: string;
  author: string;
  views: number;
  comments: number;
}

export function ImageCard({ image, title, author, views, comments, link }: ImageCardProps) {
  const classes = useStyles();

  return (
    <Card
      padding="lg"
      shadow="lg"
      className={classes.card}
      radius="md"
      component="a"
      href={link}
      target="_blank"
    >
      <div className={classes.image} style={{ backgroundImage: `url(${image})` }} />
      <div className={classes.overlay} />

      <div className={classes.content}>
        <div>
          <Text size="lg" className={classes.title} weight={500}>
            {title}
          </Text>

          <Group className={classes.body} position="apart">
            <Text size="sm" className={classes.bodyText}>
              {author}
            </Text>

            <Group spacing="lg" style={{ marginRight: 0 }}>
              <Center>
                <EyeOpenIcon style={{ marginRight: 7, width: 12, height: 12 }} />
                <Text size="sm" className={classes.bodyText}>
                  {views}
                </Text>
              </Center>
              <Center>
                <ChatBubbleIcon style={{ marginRight: 7, width: 12, height: 12 }} />
                <Text size="sm" className={classes.bodyText}>
                  {comments}
                </Text>
              </Center>
            </Group>
          </Group>
        </div>
      </div>
    </Card>
  );
}
